import React from 'react';
import useQueryBots from './useQueryBots';

function BotCollection({addBToArmy }) {
  // Map through the 'bots' array and create a list of bot elements
  const {bots}=useQueryBots()
  const botList = bots.map((bot) => (
    <div key={bot.id} className="bot-card">
      <h3>{bot.name}</h3>
      <p>{bot.health}</p>
      <p>{bot.damage}</p>
      <p>{bot.armor}</p>
      <p>{bot.bot_class}</p>
      <p>{bot.catchphrase}</p>
      <img src={bot.avatar_url} alt="bot avator"/>
      <p>{bot.created_at}</p>
      <p>{bot.updated_at}</p>
      <button onClick={() => addToArmy(bot)}>Add to Army</button>
    </div>
  ));

  return (
    <div>
      <h2>Available Bots</h2>
      {botList}
    </div>
  );
}

export default BotCollection;
