import React from "react";

function YourBotArmy({  onDelete }) {
  const [botArmy, setBotArmy] = useState([]);

  const addBotToArmy = (bot) => {
    setBotArmy([...botArmy, bot]);
  };
  return (
    <div>
      {/* Display bots in the army and provide a way to delete them */}
      <h2>Your Bot Army</h2>
      {botArmy.map((bot) => (
         <div key={bot.id} className="bot-card">
         <h3>{bot.name}</h3>
         <p>{bot.health}</p>
         <p>{bot.damage}</p>
         <p>{bot.armor}</p>
         <p>{bot.bot_class}</p>
         <p>{bot.catchphrase}</p>
         <img src={bot.avatar_url} alt="bot avator"/>
         <p>{bot.created_at}</p>
         <p>{bot.updated_at}</p>
         <button onClick={() => addBotToArmy(bot)}>Add to Army</button>
       </div>
      ))}
    </div>
  );
}

export default YourBotArmy;
